import React from 'react'
import { useMediaQuery } from 'react-responsive';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
const Text = () => {
  const [open, setOpen] = useState(false);
  const isSmallScreen = useMediaQuery({ query: '(max-width: 768px)' });
  return (
    <div className='bg-gradient-to-r from-slate-400 from-10% via-gray-600  via-30% to-zinc-600 to-90% ... h-2vh  pl-4 pr-4 text-white text-xs  '>
{isSmallScreen ? (
<>
   
<Button
        onClick={() => setOpen(!open)}
        aria-controls="example-collapse-text"
        aria-expanded={open}
      >
        click
      </Button>
      <Collapse in={open}>
        <div id="example-collapse-text">
        My expertise lies in bulding dynamic web application using<br/><span style={{color:"Yellow"}}> React js </span>

        </div>
      </Collapse> 
 </>
   
  ) : (<>
    <div className=' flex justify-center'>  <span style={{color:"pink"}}>-------About me-------- </span></div> <br/>

    I'm a profitional front end developer with 2 years 
    
    
    My expertise lies in bulding dynamic web application using<br/><span style={{color:"Yellow"}}> React js </span>
     and devolping user interface
    with a focus on clean ,maintanible code .
    I stay update on industry trends and i have ability
     to lead the team effectively and drive 
     innvoation in web  application  devolpment 
     project<br/>
     </>
  )}
   </div>
  )
}

export default Text
