import React from 'react'
import { useMediaQuery } from 'react-responsive';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Collapse from 'react-bootstrap/Collapse';
const Texttow = () => {
  const [open, setOpen] = useState(false);
  const isSmallScreen = useMediaQuery({ query: '(max-width: 768px)' });
  return (<>
    <div className='text-xs md:text-sm lg:text-sm xl:text-sm 2xl:text-sm  
    bg-gradient-to-r from-gray-600  from-10% via-slate-820 via-30% to-zinc-600 to-90%  pl-4 pr-4 text-white' >

<span style={{color:"pink"}}  className=' flex justify-center'>----------Skills----------</span>

Javascript,Html,Css,React js,Redux,bootstrap,Tailwinds,<br/>
Restful api,Typescript,

{isSmallScreen ? (
  <>
     
<p
        onClick={() => setOpen(!open)}

        aria-controls="example-collapse-text"
        aria-expanded={open}
        className='text-amber-400 underline cursor-pointer'
      >
         {open?  "show less":"show more"  }
      </p>
      <Collapse in={open}>
        <div id="example-collapse-text">
        
 

 Sass, problem solving,Ui ux,Figma ,responsive screen.<br/>
I can make website shinnig with lights and animaton or any
 requred customer
  I'm experienced for send and recive APIs and I cooperate
  with smart backend with best result  

    </div>

        
      </Collapse> 
</>
) : (<>
<div> Sass,Typescript, problem solving,Ui ux,Figma,responsive screen<br/>
I can make website shinnig with lights and animaton or any
 requred customer
  I'm experienced for send and recive APIs and I cooperate
  with smart backend with best result </div>
    </>
  )}


</div>
</>
  )
}

export default Texttow
